import React from 'react';
import {Link} from 'react-router-dom';

import {ProductConsumer} from  './Context';


class Product extends React.Component {
    render() {

        const {id, title, img, price, inCart} = this.props.Product;

        return(
        <div>
            <Link to="/Detail">
                 {/* <img src={process.env.PUBLIC_URL + "/images/"+img} alt="product-img" className="product-indi" /> */}
                  <img src={Product.img} alt="img1" /> 
            </Link>

         <button  className="cartbtn" disabled={inCart ? true : 
                false} onClick={()=>{console.log('Added To Cart');
            }} >

            {inCart ? ( <p className="temp" disabled>In Cart</p> ) : ( <i className="fa fa-shopping-cart" /> ) }
         </button>
        <p>{title}</p>
        </div>
        )
    }

}

export default Product;
