import React from 'react';

export default function CartItem({item,value}) {
    const {id, img, title, price, total, count} = item;
    const {increament, decreament,removeItem} = value;
    return (
        <div className="myCart">
                <div className="container flexible">
                    <p><img src={img} style={{width: "70px",height: "auto"}} /></p>
                    <p><b>Product:</b>{title}</p>
                    <p><b>Price:</b> {price}</p>
                    <p>
                    <div className="flexible">
                        
                        <span onClick={() =>decreament(id)}>-</span>
                        <span>{count}</span>
                        <span onClick={() =>increament(id)}>+</span>
                        
                    </div>
                    </p>
                        <p><i className="fa fa-trash" /></p>
                        <p><strong>Total Amount:</strong>${count}</p>
                </div>
            </div>
    )
}
